public function update_default_card($user_id, $role_id, $card_id)
{
	$this->db->where([
		'role_id' => $role_id,
		'user_id' => $user_id
	]);
	$this->db->update($this->_table, ['is_default' => 0]);
	$this->db->where('id' , $card_id);
	return $this->db->update($this->_table, ['is_default' => 1]);
}

