public function get_last_subscription($user_id, $role_id = 0)
{
	$this->db->where('user_id', $user_id);

	if($role_id > 0)
	{
		$this->db->where('role_id', $role_id);
	}

	$this->db->order_by('id', 'DESC');
	$this->db->limit(1);
	return $this->db->get($this->_table)->row();
}

